HMIRuntime.Trace("Temperatures: " + temperatures.join("°C, "));

// Navigate to Screen - Siemens WinCC Unified
function navigateToScreen(screenName) {
    try {
        HMIRuntime.Screens.Show(screenName);
        HMIRuntime.Trace("Navigated to screen: " + screenName);
        return true;
    } catch (error) {
        HMIRuntime.Trace("Error navigating: " + error.message);
        return false;
    }
}

// Example usage:
navigateToScreen("MainOverview");
navigateToScreen("AlarmView");

// Acknowledge Alarm - Siemens WinCC Unified
function acknowledgeAlarm(alarmId) {
    try {
        HMIRuntime.Alarms.Acknowledge(alarmId);
        HMIRuntime.Trace("Acknowledged alarm: " + alarmId);
        return true;
    } catch (error) {
        HMIRuntime.Trace("Error acknowledging alarm: " + error.message);
        return false;
    }
}

// Example usage:
acknowledgeAlarm("ALM_001");
acknowledgeAlarm("TEMP_HIGH_ALARM");

