// Simulate some process time
var endTime = new Date(startTime.getTime() + 5000); // Add 5 seconds

var timeDifference = endTime - startTime; // Difference in milliseconds
var secondsDifference = timeDifference / 1000;
HMIRuntime.Trace("Process time: " + secondsDifference + " seconds");

// Calculate hours difference
