var ageInDays = Math.floor((currentDate - installDate) / (1000 * 60 * 60 * 24));
var ageInYears = Math.floor(ageInDays / 365);
HMIRuntime.Trace("Equipment age: " + ageInYears + " years (" + ageInDays + " days)");

// Time Zone Handling - Siemens WinCC Unified
