HMIRuntime.Trace("Manual UTC: " + manualUTC);

// Create UTC timestamp for global logging
var utcTimestamp = Date.UTC(now.getFullYear(), now.getMonth(), now.getDate(), now.getHours(), now.getMinutes());
HMIRuntime.Trace("UTC timestamp: " + utcTimestamp);

// String to Number Conversion - Siemens WinCC Unified
// Convert setpoint from user input
