var numbers = [];
for (var i = 0; i < valueArray.length; i++) {
    var num = parseFloat(valueArray[i]);
    if (!isNaN(num)) {
        numbers.push(num);
    }
}
HMIRuntime.Trace("Numbers: " + numbers.join(", "));

// Number to String Conversion - Siemens WinCC Unified
// Convert temperature for display
var temperature = 78.456;
var tempString = temperature.toString();
HMIRuntime.Trace("Temperature as string: " + tempString);

// Format with specific decimal places
var pressure = 123.456789;
var pressureString = pressure.toFixed(2);
HMIRuntime.Trace("Pressure: " + pressureString + " bar");

// Build display message with numbers
var motorName = "Motor 1";
var speed = 1450;
var current = 8.5;
var statusMessage = motorName + " running at " + speed.toString() + " RPM, " + current.toString() + " A";
HMIRuntime.Trace("Status: " + statusMessage);

// Convert to scientific notation
var largeNumber = 1234567.89;
var scientificString = largeNumber.toExponential(2);
HMIRuntime.Trace("Scientific: " + scientificString);

// Convert to fixed width string (padding)
var count = 42;
var countString = count.toString();
while (countString.length < 4) {
    countString = "0" + countString; // Add leading zeros
}
HMIRuntime.Trace("Padded count: " + countString);

// Boolean Conversion - Siemens WinCC Unified
// Convert PLC values to boolean
var plcValue1 = 1;  // PLC sends 1 for true
var isRunning = Boolean(plcValue1);
HMIRuntime.Trace("Motor running: " + isRunning);

// Convert string values
var stringValue = "true";
var boolValue = (stringValue.toLowerCase() === "true");
HMIRuntime.Trace("String to bool: " + boolValue);

// Handle different PLC formats
var plcStatus = "ON";  // PLC might send ON/OFF
var isOn = (plcStatus.toUpperCase() === "ON");
HMIRuntime.Trace("Equipment on: " + isOn);

// Convert numbers to boolean (0 = false, anything else = true)
var values = [0, 1, 5, -1, 0.5];
for (var i = 0; i < values.length; i++) {
    var boolResult = Boolean(values[i]);
    HMIRuntime.Trace("Value " + values[i] + " as boolean: " + boolResult);
}

// Convert boolean back to PLC format
var motorEnabled = true;
var plcFormat = motorEnabled ? 1 : 0;  // Convert to 1/0 for PLC
HMIRuntime.Trace("PLC format: " + plcFormat);

// Handle null and undefined
var undefinedValue;
var nullValue = null;
HMIRuntime.Trace("Undefined as boolean: " + Boolean(undefinedValue));
HMIRuntime.Trace("Null as boolean: " + Boolean(nullValue));

// For Loops with Arrays - Siemens WinCC Unified
// Process multiple motor tags
var motorTags = ["MOTOR_001_Speed", "MOTOR_002_Speed", "MOTOR_003_Speed"];
for (var i = 0; i < motorTags.length; i++) {
    var tagName = motorTags[i];
    var speed = Tags(tagName).Read();
    HMIRuntime.Trace("Motor " + (i + 1) + " speed: " + speed + " RPM");
}

// Check multiple alarm states
var alarmTags = ["ALM_TEMP_HIGH", "ALM_PRESSURE_LOW", "ALM_MOTOR_FAULT"];
var activeAlarms = 0;
for (var i = 0; i < alarmTags.length; i++) {
    var alarmState = Tags(alarmTags[i]).Read();
    if (alarmState === true) {
        activeAlarms = activeAlarms + 1;
        HMIRuntime.Trace("Active alarm: " + alarmTags[i]);
    }
}
HMIRuntime.Trace("Total active alarms: " + activeAlarms);

// Update multiple setpoints
var setpointTags = ["SP_TEMP_1", "SP_TEMP_2", "SP_TEMP_3"];
var newSetpoint = 75.0;
for (var i = 0; i < setpointTags.length; i++) {
    Tags(setpointTags[i]).Write(newSetpoint);
    HMIRuntime.Trace("Updated " + setpointTags[i] + " to " + newSetpoint);
}

// Process equipment status list
var equipment = ["PUMP_001", "VALVE_002", "MOTOR_003"];
for (var i = 0; i < equipment.length; i++) {
    var statusTag = equipment[i] + "_Status";
    var status = Tags(statusTag).Read();
    HMIRuntime.Trace(equipment[i] + " status: " + status);
}

// While Loops - Siemens WinCC Unified
// Wait for motor to start (with timeout for safety)
var motorRunning = false;
var timeout = 0;
var maxTimeout = 10; // 10 seconds maximum wait

while (motorRunning === false && timeout < maxTimeout) {
    motorRunning = Tags("MOTOR_001_Running").Read();
    if (motorRunning === false) {
        HMIRuntime.Trace("Waiting for motor to start... (" + timeout + "s)");
        // In real application, use a timer instead of blocking
        timeout = timeout + 1;
    }
}

if (motorRunning) {
    HMIRuntime.Trace("Motor started successfully!");
} else {
    HMIRuntime.Trace("Timeout: Motor failed to start");
}

// Process queue until empty
var queueLength = Tags("QUEUE_LENGTH").Read();
while (queueLength > 0) {
    HMIRuntime.Trace("Processing queue item, remaining: " + queueLength);
    // Process one item (this would trigger PLC logic)
    Tags("PROCESS_NEXT_ITEM").Write(true);

    // Check queue length again
    queueLength = Tags("QUEUE_LENGTH").Read();
}
HMIRuntime.Trace("Queue processing complete");

// Wait for stable temperature reading
var temperature = Tags("TEMP_SENSOR_1").Read();
var previousTemp = temperature;
var stableCount = 0;
var requiredStableReadings = 3;

while (stableCount < requiredStableReadings) {
    temperature = Tags("TEMP_SENSOR_1").Read();
