// Error Logging Strategies - Siemens WinCC Unified
// Enhanced error logging function
function logError(errorType, location, message, additionalData) {
    var timestamp = new Date().toISOString();
    var logEntry = "[" + timestamp + "] ERROR - " + errorType + " in " + location + ": " + message;

    if (additionalData) {
        logEntry = logEntry + " | Data: " + JSON.stringify(additionalData);
    }

    // Log to HMI Runtime
    HMIRuntime.Trace(logEntry);

    // Also write to error counter tag
    try {
        var errorCount = Tags("ERROR_COUNT").Read();
        Tags("ERROR_COUNT").Write(errorCount + 1);
        Tags("LAST_ERROR_TIME").Write(timestamp);
        Tags("LAST_ERROR_MESSAGE").Write(message);
    } catch (tagError) {
        HMIRuntime.Trace("Failed to update error tags: " + tagError.message);
    }
}

