// Retry Logic - Siemens WinCC Unified
// Retry function with configurable attempts
function retryOperation(operationFunction, maxRetries, delayMs) {
    var attempt = 0;

    while (attempt < maxRetries) {
        try {
            var result = operationFunction();
            HMIRuntime.Trace("Operation successful on attempt " + (attempt + 1));
            return result;
        } catch (error) {
            attempt = attempt + 1;
            HMIRuntime.Trace("Attempt " + attempt + " failed: " + error.message);

            if (attempt >= maxRetries) {
                HMIRuntime.Trace("All " + maxRetries + " attempts failed - giving up");
                throw error; // Re-throw the last error
            }

            HMIRuntime.Trace("Waiting " + delayMs + "ms before retry...");
            // In real application, use a proper delay mechanism
            // This is simplified for demonstration
        }
    }
}

// Example: Retry tag writing
function writeTagWithRetry(tagName, value) {
    function writeOperation() {
        Tags(tagName).Write(value);
        return true;
    }

    try {
        return retryOperation(writeOperation, 3, 1000); // 3 attempts, 1 second delay
    } catch (error) {
        HMIRuntime.Trace("Failed to write " + tagName + " after all retries");
        return false;
    }
}

// Example: Retry screen navigation
function navigateWithRetry(screenName) {
    function navigationOperation() {
        HMIRuntime.Screens.Show(screenName);
        return true;
    }

    try {
        return retryOperation(navigationOperation, 2, 500); // 2 attempts, 0.5 second delay
    } catch (error) {
        HMIRuntime.Trace("Failed to navigate to " + screenName + " after all retries");
        return false;
    }
}

