// Advanced Object Manipulation - Siemens WinCC Unified
// Batch update objects with same prefix
function updateMotorObjects(motorNumber, status, speed) {
    var prefix = "Motor" + motorNumber + "_";
    var objects = [prefix + "Status", prefix + "Speed", prefix + "Icon"];

    for (var i = 0; i < objects.length; i++) {
        var obj = Screen.FindItem(objects[i]);
        if (obj) {
            if (objects[i].indexOf("Status") !== -1) {
                obj.Text = status;
            } else if (objects[i].indexOf("Speed") !== -1) {
                obj.Text = speed + " RPM";
            } else if (objects[i].indexOf("Icon") !== -1) {
                obj.BackColor = (status === "RUNNING") ? 0xFF00FF00 : 0xFFFF0000;
            }
            HMIRuntime.Trace("Updated " + objects[i]);
        }
    }
}

