// Create object state management
var objectStates = {
    alarmState: {
        objects: ['AlarmIcon', 'AlarmText', 'AlarmPanel'],
        active: {
            visible: true,
            color: 0xFFFF0000,
            text: "ALARM ACTIVE"
        },
        inactive: {
            visible: false,
            color: 0xFF808080,
            text: "SYSTEM OK"
        }
    }
};

function updateObjectGroup(groupName, isActive) {
    var group = objectStates[groupName];
    if (!group) return;

    var state = isActive ? group.active : group.inactive;

    for (var i = 0; i < group.objects.length; i++) {
        var obj = Screen.FindItem(group.objects[i]);
        if (obj) {
            obj.Visible = state.visible;
            obj.BackColor = state.color;
            if (obj.Text !== undefined) {
                obj.Text = state.text;
            }
            HMIRuntime.Trace("Updated " + group.objects[i] + " for state: " + (isActive ? "active" : "inactive"));
        }
    }
}

