// Alarm Subscription - Siemens WinCC Unified
// Create alarm subscription with filtering
var alarmSubscription = HMIRuntime.Alarming.CreateSubscription();

// Filter for Warning class alarms only
alarmSubscription.Filter = 'AlarmClassName="Warning"';
alarmSubscription.Language = 1033; // English

// Handle incoming alarms
alarmSubscription.OnAlarm = function(ErrorCode, SystemNames, ResultSet) {
    if (ErrorCode === 0) {
        HMIRuntime.Trace("Received " + ResultSet.length + " warning alarms");

        for (var index = 0; index < ResultSet.length; index++) {
            var alarm = ResultSet[index];
            HMIRuntime.Trace("Alarm " + (index + 1) + ": " + alarm.Name);
            HMIRuntime.Trace("State: " + alarm.State);
            HMIRuntime.Trace("Message: " + alarm.MessageText);

            // Update alarm display
            Screen.Items('AlarmList').Text += alarm.Name + " - " + alarm.State + "\n";
        }
    } else {
        HMIRuntime.Trace("Alarm subscription error: " + ErrorCode);
    }
};

// Start the subscription
alarmSubscription.Start();
HMIRuntime.Trace("Alarm subscription started for Warning class");

// Example 2: Critical alarms subscription
var criticalAlarms = HMIRuntime.Alarming.CreateSubscription();
criticalAlarms.Filter = 'AlarmClassName="Critical"';
criticalAlarms.Language = 1033;

criticalAlarms.OnAlarm = function(ErrorCode, SystemNames, ResultSet) {
    if (ErrorCode === 0 && ResultSet.length > 0) {
        // Flash critical alarm indicator
        Screen.Items('CriticalAlarmIcon').BackColor = 0xFFFF0000; // Red
        Screen.Items('CriticalAlarmIcon').Visible = true;

        // Count critical alarms
        var criticalCount = 0;
        for (var i = 0; i < ResultSet.length; i++) {
            if (ResultSet[i].State === "Active") {
                criticalCount++;
            }
        }

        Screen.Items('CriticalAlarmCount').Text = criticalCount.toString();
        Tags("CRITICAL_ALARM_COUNT").Write(criticalCount);

        HMIRuntime.Trace("Critical alarms active: " + criticalCount);
    }
};

criticalAlarms.Start();

// Function to stop subscriptions
function stopAlarmSubscriptions() {
    alarmSubscription.Stop();
    criticalAlarms.Stop();
    HMIRuntime.Trace("All alarm subscriptions stopped");
}

