// Count objects by type
function countObjectTypes() {
    var typeCounts = {
        buttons: 0,
        labels: 0,
        inputs: 0,
        indicators: 0,
        others: 0
    };

    for (var i in Screen.Items) {
        var objectName = Screen.Items(Number(i)).Name.toLowerCase();

        if (objectName.indexOf('button') !== -1 || objectName.indexOf('btn') !== -1) {
            typeCounts.buttons++;
        } else if (objectName.indexOf('label') !== -1 || objectName.indexOf('text') !== -1) {
            typeCounts.labels++;
        } else if (objectName.indexOf('input') !== -1 || objectName.indexOf('field') !== -1) {
            typeCounts.inputs++;
        } else if (objectName.indexOf('indicator') !== -1 || objectName.indexOf('light') !== -1) {
            typeCounts.indicators++;
        } else {
            typeCounts.others++;
        }
    }

    HMIRuntime.Trace("Object count by type:");
    HMIRuntime.Trace("  Buttons: " + typeCounts.buttons);
    HMIRuntime.Trace("  Labels: " + typeCounts.labels);
    HMIRuntime.Trace("  Inputs: " + typeCounts.inputs);
    HMIRuntime.Trace("  Indicators: " + typeCounts.indicators);
    HMIRuntime.Trace("  Others: " + typeCounts.others);

    return typeCounts;
}

