// UDT Access - Siemens WinCC Unified
// Access individual elements of User Defined Types (UDTs)

// Example UDT structure: Motor_UDT with elements:
// - Name (String)
// - Speed (Real)
// - Current (Real)
// - Temperature (Real)
// - Running (Bool)
// - FaultCode (Int)

// Read UDT elements individually
function readMotorUDT(motorInstance) {
    try {
        // Access UDT elements using dot notation: UDT_Instance.Element_Name
        var motorName = Tags(motorInstance + '.Name').Read();
        var motorSpeed = Tags(motorInstance + '.Speed').Read();
        var motorCurrent = Tags(motorInstance + '.Current').Read();
        var motorTemp = Tags(motorInstance + '.Temperature').Read();
        var isRunning = Tags(motorInstance + '.Running').Read();
        var faultCode = Tags(motorInstance + '.FaultCode').Read();

        HMIRuntime.Trace("Motor UDT Data for " + motorInstance + ":");
        HMIRuntime.Trace("  Name: " + motorName);
        HMIRuntime.Trace("  Speed: " + motorSpeed + " RPM");
        HMIRuntime.Trace("  Current: " + motorCurrent + " A");
        HMIRuntime.Trace("  Temperature: " + motorTemp + "°C");
        HMIRuntime.Trace("  Running: " + isRunning);
        HMIRuntime.Trace("  Fault Code: " + faultCode);

        return {
            name: motorName,
            speed: motorSpeed,
            current: motorCurrent,
            temperature: motorTemp,
            running: isRunning,
            faultCode: faultCode
        };
    } catch (error) {
        HMIRuntime.Trace("Error reading motor UDT: " + error.message);
        return null;
    }
}

// Write to UDT elements
function updateMotorSetpoints(motorInstance, newSpeed, startMotor) {
    try {
        Tags(motorInstance + '.SpeedSetpoint').Write(newSpeed);
        Tags(motorInstance + '.StartCommand').Write(startMotor);

        HMIRuntime.Trace("Motor " + motorInstance + " updated - Speed: " + newSpeed + ", Start: " + startMotor);
        return true;
    } catch (error) {
        HMIRuntime.Trace("Error updating motor UDT: " + error.message);
        return false;
    }
}

// Process multiple motor UDTs
function checkAllMotors() {
    var motorInstances = ["Motor_001", "Motor_002", "Motor_003"];
    var runningMotors = 0;
    var faultyMotors = [];

    for (var i = 0; i < motorInstances.length; i++) {
        var motorData = readMotorUDT(motorInstances[i]);
        if (motorData) {
            if (motorData.running) {
                runningMotors++;
            }
            if (motorData.faultCode !== 0) {
                faultyMotors.push(motorInstances[i] + " (Code: " + motorData.faultCode + ")");
            }
        }
    }

    HMIRuntime.Trace("Motor Summary - Running: " + runningMotors + "/" + motorInstances.length);
    if (faultyMotors.length > 0) {
        HMIRuntime.Trace("Motors with faults: " + faultyMotors.join(", "));
    }
}

// Tank UDT example
function readTankUDT(tankInstance) {
    try {
        var level = Tags(tankInstance + '.Level').Read();
        var temperature = Tags(tankInstance + '.Temperature').Read();
        var pressure = Tags(tankInstance + '.Pressure').Read();
        var valveOpen = Tags(tankInstance + '.OutletValve').Read();

        HMIRuntime.Trace("Tank " + tankInstance + " - Level: " + level + "%, Temp: " + temperature + "°C, Pressure: " + pressure + " bar");

        return {level: level, temperature: temperature, pressure: pressure, valveOpen: valveOpen};
    } catch (error) {
        HMIRuntime.Trace("Error reading tank UDT: " + error.message);
        return null;
    }
}

