            case 2: // Down swipe
                handleSwipeDown();
                break;
            case 3: // Left swipe
                handleSwipeLeft();
                break;
            case 4: // Right swipe
                handleSwipeRight();
                break;
            default:
                HMIRuntime.Trace("No direction detected or tap gesture");
                break;
        }
    } catch (error) {
        HMIRuntime.Trace("Error reading touch direction: " + error.message);
    }
}

