// High-Precision Timestamps - Siemens WinCC Unified
// Use Math.DatePrecise for nanosecond precision (WinCC-specific)

// Create high-precision timestamp
function createPreciseTimestamp() {
    try {
        // Math.DatePrecise provides 100ns resolution as 64-bit integer
        var preciseTime = new Math.DatePrecise();
        var timestampValue = preciseTime.Value; // 64-bit integer value

        HMIRuntime.Trace("Precise timestamp (100ns ticks): " + timestampValue);
        return preciseTime;
    } catch (error) {
        HMIRuntime.Trace("High-precision timestamps not available: " + error.message);
        // Fallback to standard Date
        return new Date();
    }
}

// Log events with precise timing
function logPreciseEvent(eventName, eventData) {
    var timestamp = createPreciseTimestamp();
    var standardTime = new Date().toISOString();

    var logEntry = {
        event: eventName,
        data: eventData,
        preciseTime: timestamp.Value || timestamp.getTime(),
        standardTime: standardTime
    };

    HMIRuntime.Trace("PRECISE LOG: " + JSON.stringify(logEntry));
    return logEntry;
}

// Measure precise duration between events
var preciseTimer = {
    startTime: null,

    start: function() {
        this.startTime = createPreciseTimestamp();
        HMIRuntime.Trace("Precise timer started");
    },

    stop: function(operationName) {
        if (!this.startTime) {
            HMIRuntime.Trace("Timer not started");
            return null;
        }

        var endTime = createPreciseTimestamp();
        var duration;

        if (this.startTime.Value && endTime.Value) {
            // Calculate duration in 100ns ticks
            duration = endTime.Value - this.startTime.Value;
            var milliseconds = duration / 10000; // Convert to milliseconds
            HMIRuntime.Trace("Operation '" + operationName + "' took " + milliseconds.toFixed(3) + " ms (precise)");
        } else {
            // Fallback to standard timing
            duration = endTime.getTime() - this.startTime.getTime();
            HMIRuntime.Trace("Operation '" + operationName + "' took " + duration + " ms (standard)");
        }

        this.startTime = null;
        return duration;
    }
};

// High-frequency data logging with precise timestamps
function logHighFrequencyData() {
    var timestamp = createPreciseTimestamp();

    // Simulate reading multiple sensors rapidly
    var sensorData = {
        timestamp: timestamp.Value || timestamp.getTime(),
        motor1Speed: Tags("MOTOR_001_Speed").Read(),
        motor1Current: Tags("MOTOR_001_Current").Read(),
        pressure: Tags("PRESSURE_SENSOR_1").Read(),
        temperature: Tags("TEMP_SENSOR_1").Read()
    };

    // In real application, this would be written to high-speed logging system
    HMIRuntime.Trace("High-freq data: " + JSON.stringify(sensorData));
    return sensorData;
}

// Synchronize with PLC timestamps (if PLC also uses nanosecond precision)
function synchronizeWithPLC() {
    var hmiTime = createPreciseTimestamp();
    var plcTime = Tags("PLC_PRECISE_TIMESTAMP").Read(); // Assuming PLC provides precise timestamp

    var timeDiff;
    if (hmiTime.Value) {
        timeDiff = Math.abs(hmiTime.Value - plcTime) / 10000; // Convert to milliseconds
    } else {
        timeDiff = Math.abs(hmiTime.getTime() - plcTime);
    }

    HMIRuntime.Trace("Time synchronization - HMI/PLC difference: " + timeDiff.toFixed(3) + " ms");

    if (timeDiff > 10) { // More than 10ms difference
        HMIRuntime.Trace("WARNING: Significant time difference detected");
    }

    return timeDiff;
}

// Performance measurement for critical operations
function measureCriticalOperation() {
    preciseTimer.start();

    // Simulate critical operation
    var tagSet = Tags.CreateTagSet(["MOTOR_001_Speed", "MOTOR_002_Speed", "MOTOR_003_Speed"]);
    tagSet.Read();

    // Process the data
    for (var i = 0; i < 3; i++) {
        var tagName = "MOTOR_00" + (i + 1) + "_Speed";
        var speed = tagSet.Item(tagName).Value;
        // Perform calculations
        var efficiency = speed / 1800 * 100;
    }

    var duration = preciseTimer.stop("TagSet read and processing");

    // Log if operation takes too long
    if (duration && duration > 50000) { // More than 5ms in 100ns ticks
        HMIRuntime.Trace("PERFORMANCE WARNING: Critical operation slow");
    }
}

