// Create database connection (store in session for reuse)
function createDatabaseConnection() {
    try {
        // Check if connection already exists in session
        if (HMIRuntime.UI.DataSet.Exists('DB_CONNECTION')) {
            HMIRuntime.Trace("Using existing database connection");
            return HMIRuntime.UI.DataSet.Get('DB_CONNECTION').Value;
