        // Create new database connection
        var connectionString = "Server=production-db;Database=WinCC_Data;Trusted_Connection=true;";
        var dbConnection = HMIRuntime.CreateDatabaseConnection(connectionString);

        // Store connection in session for reuse (better performance)
        HMIRuntime.UI.DataSet.Add('DB_CONNECTION', dbConnection);

        HMIRuntime.Trace("Database connection established and stored in session");
        return dbConnection;
    } catch (error) {
        HMIRuntime.Trace("Database connection failed: " + error.message);
        return null;
    }
}

// Insert production data into database
function logProductionData(motorId, speed, current, temperature, timestamp) {
    var connection = createDatabaseConnection();
    if (!connection) return false;

    try {
        var query = "INSERT INTO ProductionLog (MotorID, Speed, Current, Temperature, Timestamp) " +
                   "VALUES ('" + motorId + "', " + speed + ", " + current + ", " + temperature + ", '" + timestamp + "')";

        var result = connection.ExecuteNonQuery(query);
        HMIRuntime.Trace("Production data logged for " + motorId + ": " + result + " rows affected");
        return true;
    } catch (error) {
        HMIRuntime.Trace("Failed to log production data: " + error.message);
        return false;
    }
}

// Query production statistics
function getProductionStatistics(startDate, endDate) {
    var connection = createDatabaseConnection();
    if (!connection) return null;

    try {
        var query = "SELECT MotorID, COUNT(*) as RecordCount, AVG(Speed) as AvgSpeed, " +
                   "AVG(Current) as AvgCurrent, MAX(Temperature) as MaxTemp " +
                   "FROM ProductionLog " +
                   "WHERE Timestamp BETWEEN '" + startDate + "' AND '" + endDate + "' " +
                   "GROUP BY MotorID";

        var resultSet = connection.ExecuteQuery(query);

        HMIRuntime.Trace("Production statistics query executed");

        // Process results
        var statistics = [];
        while (resultSet.Read()) {
            var stat = {
                motorId: resultSet.GetString("MotorID"),
                recordCount: resultSet.GetInt32("RecordCount"),
                avgSpeed: resultSet.GetDouble("AvgSpeed").toFixed(1),
                avgCurrent: resultSet.GetDouble("AvgCurrent").toFixed(2),
                maxTemp: resultSet.GetDouble("MaxTemp").toFixed(1)
            };
            statistics.push(stat);

            HMIRuntime.Trace("Motor " + stat.motorId + ": " + stat.recordCount + " records, Avg Speed: " + stat.avgSpeed + " RPM");
        }

        return statistics;
    } catch (error) {
        HMIRuntime.Trace("Failed to query production statistics: " + error.message);
        return null;
    }
}

// Get alarm history from database
function getAlarmHistory(hours) {
    var connection = createDatabaseConnection();
    if (!connection) return null;

    try {
        var query = "SELECT AlarmID, AlarmText, Severity, Timestamp, AcknowledgedBy " +
                   "FROM AlarmHistory " +
                   "WHERE Timestamp >= DATEADD(hour, -" + hours + ", GETDATE()) " +
                   "ORDER BY Timestamp DESC";

        var resultSet = connection.ExecuteQuery(query);

        var alarms = [];
        while (resultSet.Read()) {
            var alarm = {
                id: resultSet.GetString("AlarmID"),
                text: resultSet.GetString("AlarmText"),
                severity: resultSet.GetString("Severity"),
                timestamp: resultSet.GetDateTime("Timestamp"),
                acknowledgedBy: resultSet.GetString("AcknowledgedBy")
            };
            alarms.push(alarm);
        }

        HMIRuntime.Trace("Retrieved " + alarms.length + " alarms from last " + hours + " hours");
        return alarms;
    } catch (error) {
        HMIRuntime.Trace("Failed to get alarm history: " + error.message);
        return null;
    }
}

