// Export production report to database
function generateProductionReport() {
    var connection = createDatabaseConnection();
    if (!connection) return false;

    try {
        // Get current production data
        var totalProduction = Tags("TOTAL_PRODUCTION_COUNT").Read();
        var efficiency = Tags("OVERALL_EFFICIENCY").Read();
        var runningHours = Tags("SYSTEM_RUNNING_HOURS").Read();
        var timestamp = new Date().toISOString();

        var query = "INSERT INTO DailyReports (Date, TotalProduction, Efficiency, RunningHours, GeneratedAt) " +
                   "VALUES ('" + timestamp.split('T')[0] + "', " + totalProduction + ", " + efficiency + ", " + runningHours + ", '" + timestamp + "')";

        var result = connection.ExecuteNonQuery(query);
        HMIRuntime.Trace("Daily production report generated: " + totalProduction + " units, " + efficiency + "% efficiency");
        return true;
    } catch (error) {
        HMIRuntime.Trace("Failed to generate production report: " + error.message);
        return false;
    }
}

// Close database connection when session ends
function closeDatabaseConnection() {
    try {
        if (HMIRuntime.UI.DataSet.Exists('DB_CONNECTION')) {
            var connection = HMIRuntime.UI.DataSet.Get('DB_CONNECTION').Value;
            connection.Close();
            HMIRuntime.UI.DataSet.Remove('DB_CONNECTION');
            HMIRuntime.Trace("Database connection closed");
        }
    } catch (error) {
        HMIRuntime.Trace("Error closing database connection: " + error.message);
    }
}

