// TagSet Operations - Siemens WinCC Unified
// Efficient way to read multiple tags at once
function readMultipleTagsEfficient() {
    try {
        // Create TagSet with multiple tag names
        var tagSet = Tags.CreateTagSet(["MOTOR_001_Speed", "MOTOR_001_Current", "MOTOR_001_Temperature"]);

        // Read all tags in one operation (much faster than individual reads)
        tagSet.Read();

        // Access the values
        var speed = tagSet.Item("MOTOR_001_Speed").Value;
        var current = tagSet.Item("MOTOR_001_Current").Value;
        var temperature = tagSet.Item("MOTOR_001_Temperature").Value;

        HMIRuntime.Trace("Motor Status - Speed: " + speed + " RPM, Current: " + current + " A, Temp: " + temperature + "°C");

        return {speed: speed, current: current, temperature: temperature};
    } catch (error) {
        HMIRuntime.Trace("Error reading TagSet: " + error.message);
        return null;
    }
}

// Compare: Inefficient individual tag reading (NOT recommended)
function readMultipleTagsInefficient() {
    // This is slower - multiple individual read operations
    var speed = Tags("MOTOR_001_Speed").Read();
    var current = Tags("MOTOR_001_Current").Read();
    var temperature = Tags("MOTOR_001_Temperature").Read();

    HMIRuntime.Trace("Individual reads completed (slower method)");
}

