// Alert Dialogs - Siemens WinCC Unified
// NOTE: alert() function is NOT available in WinCC Unified for security reasons
// Use these alternatives instead:

// 1. Use HMIRuntime.Trace for debugging messages
function showDebugMessage(message) {
    HMIRuntime.Trace("DEBUG: " + message);
    // This appears in TraceViewer for diagnostics
}

// 2. Update screen objects to show status
function showStatusOnScreen(message, isError) {
    var statusLabel = Screen.FindItem('StatusMessage');
    if (statusLabel) {
        statusLabel.Text = message;
        statusLabel.ForeColor = isError ? 0xFFFF0000 : 0xFF000000; // Red for errors, black for normal
    }

    // Also log the message
    HMIRuntime.Trace(isError ? "ERROR: " + message : "INFO: " + message);
}

// 3. Use alarm system for critical notifications
function triggerSystemAlarm(alarmText, priority) {
    // This would integrate with WinCC alarm system
    Tags("SYSTEM_ALARM_TEXT").Write(alarmText);
    Tags("SYSTEM_ALARM_PRIORITY").Write(priority);
    Tags("SYSTEM_ALARM_TRIGGER").Write(true);

    HMIRuntime.Trace("ALARM TRIGGERED: " + alarmText);
}

// 4. Show popup screen for important messages
function showImportantMessage(title, message) {
    // Store message data for popup screen
    Tags("POPUP_TITLE").Write(title);
    Tags("POPUP_MESSAGE").Write(message);

    // Open popup screen
    try {
        HMIRuntime.Screens.Show("MessagePopup");
        HMIRuntime.Trace("Message popup shown: " + title);
    } catch (error) {
        HMIRuntime.Trace("Failed to show popup: " + error.message);
        // Fallback to trace message
        HMIRuntime.Trace("MESSAGE: " + title + " - " + message);
    }
}

